// Get the correct dripSequence from your input structure
const data = $json;
const dripSequence = data.output ? data.output.DripSequence : data.DripSequence;

if (!dripSequence) {
  throw new Error("DripSequence not found in the input data.");
}

let html = `
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Follow Up Sequence</title>
  <style>
    /* Reset styles for email clients */
    body {
      margin: 0;
      padding: 0;
      font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, 'Helvetica Neue', Arial, sans-serif;
      line-height: 1.5;
      background-color: #f9fafb;
    }
    /* Container styles */
    .container {
      max-width: 600px;
      margin: 20px auto;
      padding: 0 20px;
    }
    /* Table-based header for better email compatibility */
    .step-header-table {
      width: 100%;
    }
    .step-header-table td {
      vertical-align: middle;
    }
    .step-title {
      font-weight: 500;
      color: #ffffff;
    }
    .step-day {
      font-size: 14px;
      opacity: 0.9;
      color: #ffffff;
      text-align: right;
      white-space: nowrap; /* Prevent line breaks */
    }
    /* Step card styles */
    .step-card {
      background-color: #ffffff;
      border: 1px solid #e5e7eb;
      border-radius: 8px;
      overflow: hidden;
      margin-bottom: 16px;
    }
    .step-content {
      padding: 16px;
    }
    .content-section {
      margin-bottom: 12px;
    }
    .content-label {
      font-size: 14px;
      font-weight: 500;
      color: #6b7280;
      margin-bottom: 4px;
    }
    .content-text {
      color: #111827;
      background-color: #f9fafb;
      padding: 12px;
      border-radius: 8px;
      white-space: pre-wrap;
      font-size: 14px;
    }
    .content-notes {
      font-size: 14px;
      color: #6b7280;
    }
    /* Table-based separator for email compatibility */
    .separator-table {
      width: 100%;
      margin: 16px 0;
    }
    .separator-table td {
      border-bottom: 1px solid #e5e7eb;
      width: 45%;
    }
    .separator-table .separator-text {
      text-align: center;
      font-size: 14px;
      color: #6b7280;
      width: 10%;
      white-space: nowrap; /* Prevent line breaks in separator text */
    }
  </style>
</head>
<body>
  <div class="container">`;

// Loop over each step to build the HTML for that step
dripSequence.forEach((step, index) => {

  // Insert a separator when there's a day gap between steps
  if (index > 0) {
    const dayDifference = step.day - dripSequence[index - 1].day;
    if (dayDifference > 0) {
      html += `
      <table class="separator-table" border="0" cellspacing="0" cellpadding="0">
        <tr>
          <td></td>
          <td class="separator-text">Wait ${dayDifference} day${dayDifference > 1 ? 's' : ''}</td>
          <td></td>
        </tr>
      </table>`;
    }
  }

  // Determine emoji and header background color based on step type
  let emoji = "";
  let headerBgColor = "";
  const lowerType = step.type.toLowerCase();
  if(lowerType.includes("phone")) {
    emoji = "üìû";
    headerBgColor = "#22c55e";
  } else if(lowerType.includes("email")) {
    emoji = "‚úâÔ∏è";
    headerBgColor = "#3b82f6";
  } else if(lowerType.includes("sms")) {
    emoji = "üí¨";
    headerBgColor = "#a855f7";
  }

  // Create the step card with a table-based header for email compatibility
  html += `
    <div class="step-card">
      <div style="background-color: ${headerBgColor}; padding: 12px 16px;">
        <table class="step-header-table" border="0" cellspacing="0" cellpadding="0">
          <tr>
            <td>
              <span style="font-size: 16px;">${emoji}</span>
              <span class="step-title"> Step ${step.step}: <strong>${step.type} #${step.number}</strong></span>
            </td>
            <td class="step-day">Day ${step.day}</td>
          </tr>
        </table>
      </div>
      <div class="step-content">`;

  // Include subject if present
  if (step.subject && step.subject.trim() !== "") {
    html += `
        <div class="content-section">
          <div class="content-label">Subject</div>
          <div class="content-text">${step.subject}</div>
        </div>`;
  }

  // Include the message body
  html += `
        <div class="content-section">
          <div class="content-label">Message</div>
          <div class="content-text">${step.body}</div>
        </div>`;

  // Include notes if available
  if (step.notes && step.notes.trim() !== "") {
    html += `
        <div class="content-section">
          <div class="content-label">Notes</div>
          <div class="content-notes">${step.notes}</div>
        </div>`;
  }

  html += `
      </div>
    </div>`;
});

html += `
  </div>
</body>
</html>`;

// Return the generated HTML so it can be used by your email node
return [{ html }];
