// Get all the created task pages
const createdTasks = $input.all();

// Initialize results object
const results = {
  allTasks: [],
  tasksByCategory: {
    coreTasks: [],
    adTasks: [],
    socialTasks: [],
    emailTasks: [],
    mailerTasks: [],
    nurtureTasks: []
  },
  tasksByKeyword: {}
};

// Define keywords for task categories
const categoryKeywords = {
  // Core tasks
  coreTasks: ["Funnel Pages", "Trojan Horse Survey", "Build Automations", "CRM Sync"],

  // Ad campaign tasks
  adTasks: ["Ad Creatives", "Ad Copy", "Ad Approval", "Upload Ad Campaign", "Campaign to Meta"],

  // Social media tasks
  socialTasks: ["ManyChat", "Social Media Script", "Script to Client", "Videos for Editing", "Edited Video"],

  // Email tasks
  emailTasks: ["Email Sequence", "Winner Announcement", "Promotional Email"],

  // Mailer tasks
  mailerTasks: ["QR Code", "Mailer Copy"],

  // Nurture tasks
  nurtureTasks: ["Nurture Sequence", "Phone Script", "Lead Follow-Up"]
};

// Process each task from the created tasks
for (const task of createdTasks) {
  // Extract task details
  try {
    const taskData = task.json;

    // Get the task ID
    const taskId = taskData.id;

    // Get the task title
    const taskTitle = taskData.properties?.["Task Title"]?.title?.[0]?.plain_text || "";

    // Get the task status
    const taskStatus = taskData.properties?.Status?.status?.name || "";

    // Get the task priority
    const taskPriority = taskData.properties?.Priority?.select?.name || "";

    // Get the task assignee
    const taskAssignee = taskData.properties?.Assignee?.people?.[0]?.name || "";

    // Get the task URL
    const taskUrl = taskData.url || "";

    // Create task object
    const taskObject = {
      id: taskId,
      title: taskTitle,
      status: taskStatus,
      priority: taskPriority,
      assignee: taskAssignee,
      url: taskUrl
    };

    // Add to all tasks array
    results.allTasks.push(taskObject);

    // Categorize by task type based on keywords
    for (const [category, keywords] of Object.entries(categoryKeywords)) {
      if (keywords.some(keyword => taskTitle.includes(keyword))) {
        results.tasksByCategory[category].push(taskObject);
      }
    }

    // Create keyword-based task lookup
    const words = taskTitle.split(' ');
    for (const word of words) {
      if (word.length > 3) { // Only use meaningful words
        const cleanWord = word.toLowerCase().replace(/[^a-z0-9]/g, '');
        if (!results.tasksByKeyword[cleanWord]) {
          results.tasksByKeyword[cleanWord] = [];
        }
        results.tasksByKeyword[cleanWord].push(taskObject);
      }
    }

  } catch (error) {
    console.warn(`Error processing task: ${error.message}`);
  }
}

// Add summary counts
results.counts = {
  total: results.allTasks.length,
  core: results.tasksByCategory.coreTasks.length,
  ad: results.tasksByCategory.adTasks.length,
  social: results.tasksByCategory.socialTasks.length,
  email: results.tasksByCategory.emailTasks.length,
  mailer: results.tasksByCategory.mailerTasks.length,
  nurture: results.tasksByCategory.nurtureTasks.length
};

// Add feature flags to indicate if certain task types exist
results.hasAdTasks = results.tasksByCategory.adTasks.length > 0;
results.hasSocialTasks = results.tasksByCategory.socialTasks.length > 0;
results.hasEmailTasks = results.tasksByCategory.emailTasks.length > 0;
results.hasMailerTasks = results.tasksByCategory.mailerTasks.length > 0;

