  } catch (error) {
    console.warn("Client Deliverables ID not found. This relation will be empty.");
  }

  // --- 2. Define Page Title & Type (using Typeform data) ---
  const firstName = formData['First name'] || 'Client';
  const pageTitle = `${firstName} - Giveaway`;
  const campaignTypeValue = "Giveaway";

  // --- 3. Get Campaign Properties from Typeform ---
  // Campaign Objective (select)
  const objective = formData["What is the objective of the Ad"] || null;

  // Budget Per Day (number)
  const budget = formData["What is your daily budget for the giveaway ad campaign?"] || null;
  const budgetNumber = budget ? parseFloat(budget) : null;

  // End Date (date)
  const endDate = formData["When would you like to STOP the ad campaign"] || null;

  // Location (text)
  const location = formData["What City / Area would you like the ad campaign to target"] || null;

  // --- 4. Calculate Start Date ---
  function getBusinessDaysDueDate(businessDays) {
    let d = new Date();
    let addedDays = 0;
    while (addedDays < businessDays) {
      d.setDate(d.getDate() + 1);
      const dayOfWeek = d.getDay(); // 0 = Sunday, 6 = Saturday
      if (dayOfWeek !== 0 && dayOfWeek !== 6) {
        addedDays++;
      }
    }
    return d.toISOString().split('T')[0]; // YYYY-MM-DD
  }
  const startDateString = getBusinessDaysDueDate(3); // Today + 3 business days

  // --- 5. Generate Content Blocks (Page Body from Typeform data) ---
  const contentBlocks = [];
  contentBlocks.push({ object: "block", type: "heading_1", heading_1: { rich_text: [{ type: "text", text: { content: pageTitle } }] } });
  contentBlocks.push({ object: "block", type: "divider", divider: {} });

  for (const [question, answer] of Object.entries(formData)) {
    // Skip internal Typeform fields
     if (question.startsWith('_') || ['markdown', 'error', 'event_id', 'event_type', 'form_response'].includes(question)) {
        continue;
    }

    const formattedQuestion = question.replace(/\*/g, '').trim();
    contentBlocks.push({ object: "block", type: "heading_3", heading_3: { rich_text: [{ type: "text", text: { content: formattedQuestion } }], "color": "default" } });

    let formattedAnswer;
    if (answer === undefined || answer === null || answer === '') { formattedAnswer = 'Not provided'; }
    else if (Array.isArray(answer)) { formattedAnswer = answer.join(', '); }
    else if (typeof answer === 'object' && answer !== null && !(answer instanceof Date)) { formattedAnswer = (answer.hasOwnProperty('url') && answer.hasOwnProperty('name')) ? answer.url : JSON.stringify(answer); }
