// Transform multiple items into a single clean JSON object
function transformToCleanJson() {
  // Initialize our new structure
  const cleanJson = {};

  // Process all items
  $input.all().forEach(item => {
    // Make sure we have an item with output property
    if (item && item.json && item.json.output) {
      // Get the key from this item's output (assuming each item has a different key)
      const outputKeys = Object.keys(item.json.output);

      // Add each key-value pair to our clean JSON
      outputKeys.forEach(key => {
        cleanJson[key] = item.json.output[key];
      });
    }
  });

  // Return as a single item
  return [{ json: cleanJson }];
}

// Process all input items
return transformToCleanJson();

// Function to convert the combined JSON data to markdown
function convertToMarkdown(jsonData) {
  let markdown = '';

  // Check if we have valid data to convert
  if (!jsonData) return '# No data available';

  // Add headline section
  if (jsonData.ad_headlines && jsonData.ad_headlines.length > 0) {
    markdown += '# Ad Headlines\n\n';

    jsonData.ad_headlines.forEach(headline => {
      markdown += `## ${headline.headline}\n`;
      markdown += `*${headline.style}*\n\n`;
    });

    markdown += '---\n\n';
  }

  // Add lead-ins section
  if (jsonData.ad_lead_ins && jsonData.ad_lead_ins.length > 0) {
    markdown += '# Ad Lead-Ins\n\n';

    jsonData.ad_lead_ins.forEach(leadInGroup => {
      markdown += `## ${leadInGroup.buyer_profile}\n\n`;

      if (leadInGroup.lead_ins && leadInGroup.lead_ins.length > 0) {
        leadInGroup.lead_ins.forEach(leadIn => {
          markdown += `- **${leadIn.lead_in}** *(${leadIn.style})*\n`;
        });
        markdown += '\n';
      }
    });

    markdown += '---\n\n';
  }

  // Add body copies section
  if (jsonData.ad_body_copies && jsonData.ad_body_copies.length > 0) {
    markdown += '# Ad Body Copies\n\n';

    jsonData.ad_body_copies.forEach(bodyCopy => {
      markdown += `## For ${bodyCopy.buyer_profile}\n\n`;
      markdown += `${bodyCopy.body_copy}\n\n`;
      markdown += `**CTA:** ${bodyCopy.cta}\n\n`;
      markdown += '---\n\n';
    });
  }

  return markdown;
}

// For use in n8n JavaScript Code node
function processJSON() {
  // Get the JSON data from the input
  const inputData = $input.first().json;

  // Convert it to markdown
  const markdownOutput = convertToMarkdown(inputData);

  // Return the markdown as a string in the json property
  return [{
    json: {
      markdown: markdownOutput
    }
  }];
}

// Process the input and return markdown
return processJSON();
