// --- Code Node for Creating Service Request Page ---

// Use try...catch for robust error handling
try {
  // --- 1. Get Data from Previous Nodes ---
  const formData = $('Typeform Trigger').first().json; // Original Typeform data

  // Get the Client ID from the Merge node
  const clientId = $('Merge').first().json["notion client id"];
  if (!clientId) {
      throw new Error("Could not find 'notion client id' from Merge node.");
  }

  // Get the Campaign ID from the node where the campaign page was created
  // *** IMPORTANT: Verify this node name is correct. Is it 'Create New Listing Ad Campaign' or 'Create Client Campaign Page'? Using the one from your code block. ***

  // --- 2. Define Values for Properties ---
  let serviceRequestName; // Declare variable

  // Get the actual Client Name passed through the Merge node (ASSUMES upstream nodes pass 'clientName')
  const clientName = $('Merge').first().json["first name"];
   if (!clientName) {
      // Fallback if clientName wasn't found in Merge node output
      console.warn("Client Name ('clientName' field) not found in Merge node output. Falling back to Typeform First Name.");
      const firstNameFallback = formData['First name'] || 'Unknown Client';
      serviceRequestName = `${firstNameFallback} - Open House`;
  } else {
       // Construct the desired service request name using the Client Name
       serviceRequestName = `${clientName} - Open House`;
  }

  const requestTypeValue = "Open House"; // Set the type specifically
  const submissionDateISO = new Date().toISOString(); // Get current date/time in UTC ISO format

  // --- 3. Generate Content Blocks (Page Body from Typeform) ---
  const contentBlocks = [];

  // Add a title block to the page body for clarity
  contentBlocks.push({
    object: "block",
    type: "heading_1",
    heading_1: {
      rich_text: [{
        type: "text",
        text: { content: serviceRequestName } // Use the defined name
      }]
    }
  });

  // Add a divider
  contentBlocks.push({ object: "block", type: "divider", divider: {} });

  // Loop through original Typeform data to create Q&A blocks
  for (const [question, answer] of Object.entries(formData)) {
    // Skip internal/metadata fields
    if (question.startsWith('_') || question === 'markdown' || question === 'error' || question === 'event_id' || question === 'event_type' || question === 'form_response') {
      continue;
    }

    const formattedQuestion = question.replace(/\*/g, '').trim();

    // Add question as heading
    contentBlocks.push({
      object: "block",
      type: "heading_3",
      heading_3: {
        rich_text: [{ type: "text", text: { content: formattedQuestion } }],
         "color": "default"
      }
    });

    // Format the answer
    let formattedAnswer;
    if (answer === undefined || answer === null || answer === '') {
      formattedAnswer = 'Not provided';
    } else if (Array.isArray(answer)) {
      formattedAnswer = answer.join(', ');
    } else if (typeof answer === 'object' && answer !== null && !Array.isArray(answer) && !(answer instanceof Date)) {
       if (answer.hasOwnProperty('url') && answer.hasOwnProperty('name')) {
          formattedAnswer = answer.url; // Handle file objects
       } else {
           formattedAnswer = JSON.stringify(answer); // Stringify other objects
       }
    } else if (typeof answer === 'boolean') {
      formattedAnswer = answer ? 'Yes' : 'No';
    } else {
      formattedAnswer = String(answer);
    }

    // Check for image URLs
    const isImageUrl = typeof formattedAnswer === 'string' &&
                    (formattedAnswer.includes('api.typeform.com/responses/files') ||
                     (formattedAnswer.startsWith('http') &&
                      (formattedAnswer.endsWith('.jpg') || formattedAnswer.endsWith('.png') ||
                       formattedAnswer.endsWith('.jpeg') || formattedAnswer.endsWith('.gif'))));

    if (isImageUrl) {
      // Add image block
      contentBlocks.push({
        object: "block", type: "image", image: { type: "external", external: { url: formattedAnswer } }
      });
    } else {
      // Add text block
      contentBlocks.push({
        object: "block", type: "paragraph", paragraph: { rich_text: [{ type: "text", text: { content: formattedAnswer } }], "color": "default" }
      });
    }
     // Optional spacer
     contentBlocks.push({ object: "block", type: "paragraph", paragraph: { rich_text: [] } });
  } // End of Q&A loop

  // --- 4. Prepare Data for Notion API ---
  const notionDataPayload = {
    parent: {
      // The Database ID for your *Service Requests* database
      database_id: "1b41d08c4f5a80c1a843f04215bd0fd0"
    },
    icon: {
      type: "emoji",
      emoji: "ðŸŽ¥" // Using Gift icon as requested in previous code block
    },
    properties: {
      // Title Property: Name
      "Name": {
        title: [{ text: { content: serviceRequestName } }] // Now uses Client Name - Giveaway
      },
      // Select Property: Request Type
      "Request Type": {
        select: { // Assumes 'select' type. If multi_select, change structure
          name: requestTypeValue
        }
        /* --- If Request Type is MULTI_SELECT, use this instead: ---
        multi_select: [
          { name: requestTypeValue }
        ]
        */
      },
      // Relation Property: Client
      "Client": {
        relation: [{ id: clientId }]
      },

      // Date Property: Submission Date
      "Submission Date": {
        date: {
          start: submissionDateISO // Use the ISO UTC string
        }
      }
    },
    // Page Body Content: Children
    children: contentBlocks
  };

  // --- 5. Return the Payload ---
  // Output the data needed for the subsequent HTTP Request node
  return { json: { notionData: notionDataPayload } };

} catch (error) {
  // --- Error Handling ---
  console.error('Error preparing Service Request Notion data:', error.message);
  // Throw the error again to potentially stop the workflow or let error workflow handle it
  throw error; // Stop workflow on error
}

// Code Node: Open House Task Generator

try {
    // --- 1. Get Data from Previous Nodes ---
    const mergeOutput = $('Merge').first().json;
    const clientRelationId = mergeOutput['notion client id'];
    const clientName = mergeOutput['first name'];

    if (!clientRelationId) { throw new Error("Could not get clientRelationId from Merge node."); }
    if (!clientName) { throw new Error("Could not get 'first name' from Merge node."); }

    // --- Get Service Request ID and URL ---
    const serviceRequestNode = $('Create New Service Request');
    const serviceRequestOutput = serviceRequestNode.first();
    const serviceRelationId = serviceRequestOutput?.json?.id;
    const serviceRequestUrl = serviceRequestOutput?.json?.url;

    if (!serviceRelationId || typeof serviceRelationId !== 'string') {
        throw new Error(`Failed to get a valid page ID from node 'Create New Service Request'.`);
    }

    // --- Get Original Typeform Data ---
    const formData = $('Typeform Trigger').first().json;
    if (!formData) {
        throw new Error("Could not get data from Typeform Trigger node.");
    }

    // Extract relevant information from form data
    const lastName = formData["Last name"] || "";
    const fullName = `${clientName} ${lastName}`.trim();
    const openHouseDate = formData["Date Of The Open House"] || "";
    const brandedMediaLink = formData["Please upload a link to your branded media link. "] || "";
    const propertyListingLink = formData["Please include a link for the property listing"] || "";
    const floorPlanLink = formData["Please include a link to the Floor Plan. "] || "";
    const comparablesLink = formData["Please include a link to the On-Market Comparables & Recent Neighborhood Sales"] || "";
    const virtualTourLink = formData["Please include a link to the Virtual Tour. "] || "";
    const benefit1 = formData["What is the #1 benefit of this home? "] || "";
    const benefit2 = formData["What is the #2 benefit of this home? "] || "";
    const benefit3 = formData["What is the #3 benefit of this home? "] || "";
    const keyFeature = formData["What is the key selling feature of this house? "] || "";
    const neighborhood = formData["What should buyers know about the neighbourhood? "] || "";
    const listingPrice = formData["Listing Price "] || "";
    const beds = formData["Number of Beds "] || "";
    const baths = formData["Number of Baths "] || "";
    const homeType = formData["Home Type (condo, freehold etc..)"] || "";

    // Property summary for tasks
    const propertySummary = `${beds}bd/${baths}ba ${homeType}, $${listingPrice}`;

    // --- 2. Define Assignee IDs ---
    const assigneeMatyas = "135d872b-594c-815e-8bb4-0002be26fee3";
    const assigneeFernanda = "135d872b-594c-815e-8bb4-0002be26fee3";

    // --- 3. Due Date Calculation ---
    function getBusinessDaysDueDate(businessDays) {
        let d = new Date(); let addedDays = 0;
        if (businessDays === 0) { const day = d.getDay(); if (day !== 0 && day !== 6) return d.toISOString().split('T')[0]; else businessDays = 1; }
        while (addedDays < businessDays) { d.setDate(d.getDate() + 1); const day = d.getDay(); if (day !== 0 && day !== 6) addedDays++; }
        return d.toISOString().split('T')[0];
    }

    // Calculate based on open house date if available
    let eventDate = null;
    if (openHouseDate) {
        eventDate = new Date(openHouseDate);
    }

    const dueDateToday = getBusinessDaysDueDate(0);
    const dueDateNextDay = getBusinessDaysDueDate(1);
    const dueDate2Days = getBusinessDaysDueDate(2);
    const dueDate3Days = getBusinessDaysDueDate(3);

    // If event date is less than 5 days away, prioritize urgently
    const isUrgent = eventDate && ((eventDate - new Date()) / (1000 * 60 * 60 * 24) < 5);

    // --- 4. Helper Functions ---
    function formatBlock(type, content, options = {}) { 
        const block = { object: "block", type: type }; 
        const data = {}; 
        if (type === 'paragraph' || type.startsWith('heading') || type.endsWith('list_item') || ['toggle', 'quote', 'callout'].includes(type)) { 
            data.rich_text = options.contentRich || [{ type: "text", text: { content: content || "" } }]; 
            if(options.annotations && !options.contentRich) {data.rich_text[0].annotations = { ...options.annotations };} 
            if (options.color) data.color = options.color; 
            if (options.is_toggleable) data.is_toggleable = options.is_toggleable; 
        } else if (type === 'to_do') { 
            data.rich_text = typeof content === 'string' ? [{ type: "text", text: { content: content } }] : (Array.isArray(content) ? content : [{ type: "text", text: { content: content || "" } }]); 
            if(options.annotations && data.rich_text[0]) {data.rich_text[0].annotations = { ...options.annotations };} 
            data.checked = options.checked || false; 
            if (options.color) data.color = options.color; 
            if (options.children && Array.isArray(options.children)) { 
                data.children = options.children.map(child => formatBlock(child.type, child.content, child)); 
            } 
        } else if (type === 'image') { 
            data.type = "external"; 
            data.external = { url: options.url || content || 'https://via.placeholder.com/150' }; 
        } else if (type === 'divider') {} 

        block[type] = data; 
        return block; 
    }

    // Create service request link block
    function createServiceRequestLinkBlocks() {
        const blocks = [];
        blocks.push(formatBlock("divider", {}));
        blocks.push(formatBlock("heading_3", "Client Information"));

        if (serviceRequestUrl) {
            // Create rich text with link
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "View Client Service Request Details",
                                link: { url: serviceRequestUrl }
                            },
                            annotations: {
                                bold: true,
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        } else {
            blocks.push(formatBlock("paragraph", "Service Request URL not available"));
        }

        return blocks;
    }

    // Create form data blocks for property information
    function createPropertyDataBlocks() {
        const blocks = [];
        blocks.push(formatBlock("divider", {}));
        blocks.push(formatBlock("heading_3", "Property Information"));

        // Property Details
        blocks.push(formatBlock("paragraph", `Home Type: ${homeType}`));
        blocks.push(formatBlock("paragraph", `Beds: ${beds} | Baths: ${baths}`));
        blocks.push(formatBlock("paragraph", `Listing Price: $${listingPrice}`));

        if (openHouseDate) {
            blocks.push(formatBlock("paragraph", `Open House Date: ${openHouseDate}`));
        }

        // Property Links
        blocks.push(formatBlock("divider", {}));
        blocks.push(formatBlock("heading_3", "Property Resources"));

        // Property Listing Link
        if (propertyListingLink) {
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "Property Listing: ",
                            }
                        },
                        {
                            type: "text",
                            text: {
                                content: propertyListingLink,
                                link: { url: propertyListingLink }
                            },
                            annotations: {
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        }

        // Branded Media Link
        if (brandedMediaLink) {
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "Branded Media: ",
                            }
                        },
                        {
                            type: "text",
                            text: {
                                content: brandedMediaLink,
                                link: { url: brandedMediaLink }
                            },
                            annotations: {
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        }

        // Floor Plan Link
        if (floorPlanLink) {
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "Floor Plan: ",
                            }
                        },
                        {
                            type: "text",
                            text: {
                                content: floorPlanLink,
                                link: { url: floorPlanLink }
                            },
                            annotations: {
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        }

        // Comparables Link
        if (comparablesLink) {
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "Comparables & Neighborhood Sales: ",
                            }
                        },
                        {
                            type: "text",
                            text: {
                                content: comparablesLink,
                                link: { url: comparablesLink }
                            },
                            annotations: {
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        }

        // Virtual Tour Link
        if (virtualTourLink) {
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "Virtual Tour: ",
                            }
                        },
                        {
                            type: "text",
                            text: {
                                content: virtualTourLink,
                                link: { url: virtualTourLink }
                            },
                            annotations: {
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        }

        // Property Key Features
        blocks.push(formatBlock("divider", {}));
        blocks.push(formatBlock("heading_3", "Property Selling Points"));

        if (keyFeature) {
            blocks.push(formatBlock("paragraph", `Key Selling Feature: ${keyFeature}`));
        }

        blocks.push(formatBlock("paragraph", "Home Benefits:"));
        if (benefit1) blocks.push(formatBlock("bulleted_list_item", benefit1));
        if (benefit2) blocks.push(formatBlock("bulleted_list_item", benefit2));
        if (benefit3) blocks.push(formatBlock("bulleted_list_item", benefit3));

        if (neighborhood) {
            blocks.push(formatBlock("paragraph", `Neighborhood Info: ${neighborhood}`));
        }

        return blocks;
    }

    const serviceRequestLinkBlocks = createServiceRequestLinkBlocks();
    const propertyDataBlocks = createPropertyDataBlocks();

    // --- 5. Create Tasks Array ---
    let tasksToCreate = [
        // Task 1: Create Open House Landing Page
        { 
            taskTitle: `Open House | ${fullName} | Create Landing Page | ${propertySummary}`, 
            taskAssignee: [assigneeMatyas], 
            taskPriority: isUrgent ? "High" : "Medium", 
            taskDueDate: isUrgent ? dueDateToday : dueDate2Days, 
            taskStatus: "Ready To-Do", 
            taskBlocks: [
                formatBlock("paragraph", "Create custom landing page for open house with property details, photos, and registration form."),
                ...propertyDataBlocks,
                ...serviceRequestLinkBlocks
            ] 
        },

        // Task 2: Create Email for Open House Promotion
        { 
            taskTitle: `Open House | ${fullName} | Create Promotional Email | ${propertySummary}`, 
            taskAssignee: [assigneeFernanda], 
            taskPriority: isUrgent ? "High" : "Medium", 
            taskDueDate: isUrgent ? dueDateNextDay : dueDate2Days, 
            taskStatus: "Ready To-Do", 
            taskBlocks: [
                formatBlock("paragraph", "Write promotional email to send to database promoting the open house event."),
                formatBlock("paragraph", "Include key property features, open house date/time, and link to landing page."),
                ...propertyDataBlocks,
                ...serviceRequestLinkBlocks
            ] 
        },

        // Task 3: Send Open House Deliverables for Approval
        { 
            taskTitle: `Open House | ${fullName} | Send Deliverables for Approval | ${propertySummary}`, 
            taskAssignee: [assigneeFernanda], 
            taskPriority: isUrgent ? "High" : "Medium", 
            taskDueDate: isUrgent ? dueDate2Days : dueDate3Days, 
            taskStatus: "Ready To-Do", 
            taskBlocks: [
                formatBlock("paragraph", "Send all open house materials to client for review and approval."),
                formatBlock("paragraph", "Include landing page link, email preview, and any additional promotional materials."),
                formatBlock("paragraph", "Request feedback and confirmation before final distribution."),
                ...serviceRequestLinkBlocks
            ] 
        }
    ];

    // --- 6. Add Common Properties to Each Task ---
    tasksToCreate.forEach(task => {
        task.taskClientRelationId = [clientRelationId];
        task.taskServiceRelationId = [serviceRelationId];
        task.taskTags = ["Open House"];
    });

    // --- 7. Return the Array of Tasks ---
    return tasksToCreate;

} catch (error) {
    console.error("Error in Open House Task Generator:", error.message);
    throw error; // Re-throw error to stop workflow
}

