// Custom node for n8n that creates proper Notion page with blocks for each field
// This node transforms incoming data into Notion API-compatible format

// Get the input data from the workflow
let items = [];
if (Array.isArray($input.all())) {
  items = $input.all();
} else {
  items = [$input.item];
}

// Process each input item
const processedItems = items.map(item => {
  try {
    const data = item.json || item;

    // Construct full name from first_name and last_name fields
    const firstName = data.first_name || "";
    const lastName = data.last_name || "";
    const fullName = firstName && lastName ? `${firstName} ${lastName}` : 
                    firstName ? firstName : 
                    lastName ? lastName : "Unknown Contact";

    // Create content blocks for the Notion page
    const blocks = [];

    // Add page title
    blocks.push({
      object: "block",
      type: "heading_1",
      heading_1: {
        rich_text: [{
          type: "text",
          text: { content: `Contact Profile: ${fullName}` }
        }]
      }
    });

    // Add divider after title
    blocks.push({
      object: "block",
      type: "divider",
      divider: {}
    });

    // Define sections to organize the data
    const sections = [
      {
        title: "Personal Details",
        fields: [
          "first_name", "last_name", "phone", "email", "date_of_birth", 
          "timezone", "clothing_size", "address", "address_unit", "city", 
          "state", "postal_code", "country"
        ]
      },
      {
        title: "Brokerage Information",
        fields: [
          "brokerage_name", "brokerage_address", "brokerage_unit",
          "brokerage_city", "brokerage_state", "brokerage_postal_code", "brokerage_country"
        ]
      },
      {
        title: "Business Information",
        fields: [
          "business_type", "team_name", "team_size", "linkedin_url", "growth_obstacles", 
          "other_growth_obstacle", "deal_types", "other_deal_type", "lead_sources", 
          "other_lead_source", "transaction_types", "other_transaction_type"
        ]
      },
      {
        title: "Goals and Needs",
        fields: [
          "business_help", "program_expectations", "target_market"
        ]
      },
      {
        title: "Online Presence",
        fields: [
          "personal_instagram_handle", "has_business_instagram", "business_instagram_handle",
          "personal_facebook_url", "has_facebook_page", "facebook_page_url", 
          "has_personal_website", "personal_website_url", "has_business_website", "business_website_url", 
          "using_crm", "crm_name", "crm_connected_to_meta"
        ]
      },
      {
        title: "Branding",
        fields: [
          "primary_color", "logo_url", "headshot_url"
        ]
      },
      {
        title: "Real Estate Stats (Current Year)",
        fields: [
          "current_year_gci", "current_year_average_price", "current_year_listings", 
          "current_year_buyer_deals", "current_year_lease_deals", "current_year_commission_percentage"
        ]
      },
      {
        title: "Real Estate Stats (Next Year Goals)",
        fields: [
          "next_year_gci_goal", "next_year_target_deal_size", "next_year_target_commission_percentage"
        ]
      },
      {
        title: "Metadata",
        fields: [
          "submission_date", "source"
        ]
      }
    ];

    // Track fields that have been added to sections
    const processedFields = new Set();

    // Process each section
    sections.forEach(section => {
      // Check if this section has any fields in the data
      const sectionFields = section.fields.filter(field => {
        return field in data && 
               data[field] !== undefined && 
               data[field] !== null && 
               !(typeof data[field] === 'string' && data[field].trim() === "");
      });

      if (sectionFields.length > 0) {
        // Add section header
        blocks.push({
          object: "block",
          type: "heading_2",
          heading_2: {
            rich_text: [{
              type: "text",
              text: { content: section.title }
            }]
          }
        });

        // Add each field in this section
        sectionFields.forEach(field => {
          // Mark field as processed
          processedFields.add(field);

          // Add the field as a paragraph block
          blocks.push({
            object: "block",
            type: "paragraph",
            paragraph: {
              rich_text: [
                {
                  type: "text",
                  text: { content: formatLabel(field) + ": " },
                  annotations: { bold: true }
                },
                {
                  type: "text",
                  text: { content: formatValue(data[field]) }
                }
              ]
            }
          });
        });

        // Add divider after section
        blocks.push({
          object: "block",
          type: "divider",
          divider: {}
        });
      }
    });

    // Handle any remaining fields not covered by defined sections
    const remainingFields = Object.keys(data).filter(field => 
      !processedFields.has(field) && 
      data[field] !== undefined && 
      data[field] !== null && 
      !(typeof data[field] === 'string' && data[field].trim() === "")
    );

    if (remainingFields.length > 0) {
      // Add additional info section header
      blocks.push({
        object: "block",
        type: "heading_2",
        heading_2: {
          rich_text: [{
            type: "text",
            text: { content: "Additional Information" }
          }]
        }
      });

      // Add each remaining field
      remainingFields.forEach(field => {
        blocks.push({
          object: "block",
          type: "paragraph",
          paragraph: {
            rich_text: [
              {
                type: "text",
                text: { content: formatLabel(field) + ": " },
                annotations: { bold: true }
              },
              {
                type: "text",
                text: { content: formatValue(data[field]) }
              }
            ]
          }
        });
      });

      // Add final divider
      blocks.push({
        object: "block",
        type: "divider",
        divider: {}
      });
    }

    // Add image blocks at the bottom for logo and headshot if they exist
    if (data.logo_url || data.headshot_url) {
      // Add an Images header
      blocks.push({
        object: "block",
        type: "heading_2",
        heading_2: {
          rich_text: [{
            type: "text",
            text: { content: "Images" }
          }]
        }
      });

      // Add logo image block if exists
      if (data.logo_url && typeof data.logo_url === 'string') {
        blocks.push({
          object: "block",
          type: "heading_3",
          heading_3: {
            rich_text: [{
              type: "text",
              text: { content: "Logo" }
            }]
          }
        });

        // Use proper format for Notion API image block with URL encoding
        blocks.push({
          object: "block",
          type: "image",
          image: {
            type: "external",
            external: {
              url: encodeURI(data.logo_url.trim())
            }
          }
        });
      }

      // Add headshot image block if exists
      if (data.headshot_url && typeof data.headshot_url === 'string') {
        blocks.push({
          object: "block",
          type: "heading_3",
          heading_3: {
            rich_text: [{
              type: "text",
              text: { content: "Headshot" }
            }]
          }
        });

        // Use proper format for Notion API image block with URL encoding
        blocks.push({
          object: "block",
          type: "image",
          image: {
            type: "external",
            external: {
              url: encodeURI(data.headshot_url.trim())
            }
          }
        });
      }

      // Add final divider after images
      blocks.push({
        object: "block",
        type: "divider",
        divider: {}
      });
    }

    // Create visual summary blocks for key metrics
    blocks.push({
      object: "block",
      type: "heading_2",
      heading_2: {
        rich_text: [{
          type: "text",
          text: { content: "Performance Summary" }
        }]
      }
    });

    // Add table for current vs goal metrics
    if (data.current_year_gci || data.next_year_gci_goal) {
      // Add a callout with key performance indicators
      blocks.push({
        object: "block",
        type: "callout",
        callout: {
          icon: {
            type: "emoji",
            emoji: "ðŸ“Š"
          },
          rich_text: [{
            type: "text",
            text: { 
              content: `Current GCI: ${formatCurrency(data.current_year_gci)} â†’ Goal: ${formatCurrency(data.next_year_gci_goal)}` 
            },
            annotations: { bold: true }
          }]
        }
      });
    }

    // Prepare database properties
    const properties = {
      // Required title property for Notion
      "Name": {
        title: [
          {
            text: {
              content: fullName
            }
          }
        ]
      }
    };

    // Add common database properties
    // Only add properties that actually exist in your Notion database
    if (data.email) {
      properties["Email"] = { email: data.email };
    }

    if (data.phone) {
      properties["Phone"] = { phone_number: data.phone };
    }

    if (data.submission_date) {
      properties["Submission Date"] = {
        date: {
          start: data.submission_date
        }
      };
    }

    // Prepare final Notion API payload
    return {
      json: {
        notionData: {
          parent: {
            database_id: "1c81d08c4f5a807db365eb187c072ec1" // Set to the correct database ID
          },
          // Set icon to a person emoji
          icon: {
            type: "emoji",
            emoji: "ðŸ‘¤"
          },
          // Set cover image to the headshot if it exists
          cover: data.headshot_url ? {
            type: "external",
            external: { 
              url: encodeURI(data.headshot_url.trim())
            }
          } : undefined,
          properties: properties,
          children: blocks
        }
      }
    };
  } catch (error) {
    // Error handling
    return {
      json: {
        error: `Error processing item: ${error.message}`,
        input: item
      }
    };
  }
});

// Helper function to format field labels to be more readable
function formatLabel(fieldName) {
  return fieldName
    .replace(/_/g, ' ')
    .split(' ')
    .map(word => word.charAt(0).toUpperCase() + word.slice(1))
    .join(' ');
}

// Helper function to format business type values
function formatBusinessType(type) {
  const businessTypes = {
    'solo': 'Solo Agent',
    'leader': 'Team Leader',
    'member': 'Team Member'
  };

  return businessTypes[type] || type;
}

// Helper function to format currency values
function formatCurrency(value) {
  if (value === undefined || value === null) {
    return '$0';
  }

  return new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'USD',
    maximumFractionDigits: 0
  }).format(value);
}

// Helper function to format field values appropriately based on type
function formatValue(value) {
  if (value === undefined || value === null) {
    return 'Not provided';
  }

  if (typeof value === 'boolean') {
    return value ? 'Yes' : 'No';
  }

  if (typeof value === 'number') {
    // Format currency values
    if (String(value).length > 3 && 
        ['gci', 'price', 'deal', 'commission'].some(term => 
          String(value).toLowerCase().includes(term))) {
      return formatCurrency(value);
    }
    return value.toString();
  }

  if (typeof value === 'string') {
    // Handle dates
    if (value.match(/^\d{4}-\d{2}-\d{2}T/)) {
      try {
        // Format ISO date strings
        const date = new Date(value);
        return date.toLocaleDateString('en-US', { 
          year: 'numeric', 
          month: 'long', 
          day: 'numeric',
          hour: '2-digit',
          minute: '2-digit'
        });
      } catch (e) {
        return value;
      }
    }

    // Format simple dates (without time)
    if (value.match(/^\d{4}-\d{2}-\d{2}$/)) {
      try {
        const date = new Date(value);
        return date.toLocaleDateString('en-US', {
          year: 'numeric',
          month: 'long',
          day: 'numeric'
        });
      } catch (e) {
        return value;
      }
    }

    // Format business_type values to be more readable
    if (value === 'solo') {
      return 'Solo Agent';
    } else if (value === 'leader') {
      return 'Team Leader';
    } else if (value === 'member') {
      return 'Team Member';
    }

    return value;
  }

  if (Array.isArray(value)) {
    return value.join(', ');
  }

  if (typeof value === 'object') {
    return JSON.stringify(value);
  }

  return String(value);
}

// Return processed items
return processedItems;

