const lastName = data.last_name || '';
const clientName = (firstName + ' ' + lastName).trim() || 'New Client';

// Get logo and headshot URLs with fallbacks
const logoUrl = data.logo_url || 'https://via.placeholder.com/150?text=No+Logo';
const headshotUrl = data.headshot_url || 'https://via.placeholder.com/100?text=No+Photo';

// Format currency values
function formatCurrency(value) {
  if (!value) return '$0';
  const numValue = parseFloat(value);
  return isNaN(numValue) ? '$0' : '$' + numValue.toLocaleString('en-US');
}

// Format basic date
function formatDate(dateStr) {
  if (!dateStr) return 'N/A';
  try {
    const date = new Date(dateStr);
    return date.toLocaleDateString('en-US');
  } catch (e) {
    return dateStr || 'N/A';
  }
}

// Format address
