// Transform multiple items into a single clean JSON object
function transformToCleanJson() {
  // Initialize our new structure
  const cleanJson = {};

  // Process all items
  $input.all().forEach(item => {
    // Make sure we have an item with output property
    if (item && item.json && item.json.output) {
      // Get the key from this item's output (assuming each item has a different key)
      const outputKeys = Object.keys(item.json.output);

      // Add each key-value pair to our clean JSON
      outputKeys.forEach(key => {
        cleanJson[key] = item.json.output[key];
      });
    }
  });

  // Return as a single item
  return [{ json: cleanJson }];
}

// Process all input items
return transformToCleanJson();

const processedItems = $('All Inputs').all().map(item => {
  try {
    // Get form data
    const formData = item.json;

    // Extract form title (use Address or another primary field as the page title)
    const pageTitle = formData.Address || "Form Submission";

    // Create content blocks for Notion
    const contentBlocks = [];

    // Add title block
    contentBlocks.push({
      object: "block",
      type: "heading_1",
      heading_1: {
        rich_text: [{
          type: "text",
          text: { content: pageTitle }
        }]
      }
    });

    // Process each field in the form data
    for (const [question, answer] of Object.entries(formData)) {
      // Skip internal fields or metadata
      if (question.startsWith('_') || question === 'markdown' || question === 'error') {
        continue;
      }

      // Format the question (remove asterisks and trim)
      const formattedQuestion = question.replace(/\*/g, '').trim();

      // Add question as heading
      contentBlocks.push({
        object: "block",
        type: "heading_3",
        heading_3: {
          rich_text: [{
            type: "text",
            text: { content: formattedQuestion }
          }]
        }
      });

      // Format the answer based on its type
      let formattedAnswer;
      let blockType = "paragraph";

      if (answer === undefined || answer === null) {
        formattedAnswer = 'Not provided';
      } else if (Array.isArray(answer)) {
        formattedAnswer = answer.join(', ');
      } else if (typeof answer === 'object') {
        // Handle nested objects (like the emotions field)
        formattedAnswer = Object.entries(answer)
          .map(([key, val]) => `${key}: ${val}`)
          .join(', ');
      } else if (typeof answer === 'boolean') {
        formattedAnswer = answer ? 'Yes' : 'No';
      } else {
        formattedAnswer = String(answer);
      }

      // Check if this is an image URL
      const isImage = typeof answer === 'string' && 
                (answer.includes('api.typeform.com/responses/files') || 
                 (answer.startsWith('http') && 
                  (answer.endsWith('.jpg') || answer.endsWith('.png') || 
                   answer.endsWith('.jpeg') || answer.endsWith('.gif'))));

      if (isImage) {
        // Add image block
        contentBlocks.push({
          object: "block",
          type: "image",
          image: {
            type: "external",
            external: {
              url: answer
            }
          }
        });
      } else {
        // Add text block
        contentBlocks.push({
          object: "block",
          type: "paragraph",
          paragraph: {
            rich_text: [{
              type: "text",
              text: { content: formattedAnswer }
            }]
          }
        });
      }
    }

    // Prepare data for Notion API
    item.json.notionData = {
      parent: {
        // Replace with your actual database ID
        database_id: "1a41d08c4f5a808b8944c0faa8922503"
      },
      properties: {
        // This assumes your database has a "Name" property - adjust as needed
        "Campaign Name": {
          title: [
            {
              text: {
                content: pageTitle
              }
            }
          ]
        }
      },
      children: contentBlocks
    };

    return item;
  } catch (error) {
    // Handle errors gracefully
    console.error('Error processing form data:', error.message);
    item.json.error = error.message;
    return item;
  }
});

// Return the processed items
return processedItems;

