// Process emails from input node and format for Notion
const emails = $('Email Agent').first().json.output.emails;

// Create an array to hold all blocks for the Notion page
const notionBlocks = [];

// Add title block
notionBlocks.push({
  object: "block",
  type: "heading_1",
  heading_1: {
    rich_text: [{
      type: "text",
      text: { content: "3 Email Sequence Generated for New Listing Ad" }
    }]
  }
});

// Add description paragraph
notionBlocks.push({
  object: "block",
  type: "paragraph",
  paragraph: {
    rich_text: [{
      type: "text",
      text: { content: "Model Used: openai-o3-mini" }
    }]
  }
});

// Process each email
emails.forEach((email, emailIndex) => {
  try {
    const { Name, Subject, Body } = email;

    // Add divider between emails (except before the first one)
    if (emailIndex > 0) {
      notionBlocks.push({
        object: "block",
        type: "divider",
        divider: {}
      });
    }

    // Add the email name as heading_1
    notionBlocks.push({
      object: "block",
      type: "heading_1",
      heading_1: {
        rich_text: [{
          type: "text",
          text: { content: Name }
        }]
      }
    });

    // Add subject as paragraph with bold formatting
    notionBlocks.push({
      object: "block",
      type: "paragraph",
      paragraph: {
        rich_text: [{
          type: "text",
          text: { content: `Subject: ${Subject}` },
          annotations: {
            bold: true
          }
        }]
      }
    });

    // Process the email body
    // Split into paragraphs
    const paragraphs = Body.split('\n\n');

    paragraphs.forEach(paragraph => {
      // Check if it's a feature section heading (starts with emoji + **)
      if (paragraph.startsWith('🏡 **') || paragraph.startsWith('✨ **') || paragraph.startsWith('👉 **')) {
        // This is a features heading section - use paragraph with bold formatting instead of heading
        notionBlocks.push({
          object: "block",
          type: "paragraph",
          paragraph: {
            rich_text: [{
              type: "text",
              text: { content: paragraph.replace(/\*\*/g, '') },
              annotations: {
                bold: true
              }
            }]
          }
        });
      } else if (paragraph.includes('✔')) {
        // This is a list of features - convert to bulleted list
        const listItems = paragraph.split('\n');

        listItems.forEach(item => {
          if (item.trim()) {
            // Remove the ✔ symbol and any bold markdown
            const cleanItem = item.trim().replace(/✔ /g, '').replace(/\*\*/g, '');

            notionBlocks.push({
              object: "block",
              type: "bulleted_list_item",
              bulleted_list_item: {
                rich_text: [{
                  type: "text",
                  text: { content: cleanItem }
                }]
              }
            });
          }
        });
      } else if (paragraph.includes('**Reply')) {
        // Call to action - make it bold
        notionBlocks.push({
          object: "block",
          type: "paragraph",
          paragraph: {
            rich_text: [{
              type: "text",
              text: { content: paragraph.replace(/\*\*/g, '') },
              annotations: {
                bold: true
              }
            }]
          }
        });
      } else if (paragraph.startsWith('Best regards')) {
        // Signature section - convert newlines to rich text
        const signatureLines = paragraph.split('\n');
        const richTextArray = signatureLines.map((line, index) => ({
          type: "text",
          text: { content: index < signatureLines.length - 1 ? line + "\n" : line }
        }));

        notionBlocks.push({
          object: "block",
          type: "paragraph",
          paragraph: {
            rich_text: richTextArray
          }
        });
      } else if (paragraph.trim()) {
        // Regular paragraph
        notionBlocks.push({
          object: "block",
          type: "paragraph",
          paragraph: {
            rich_text: [{
              type: "text",
              text: { content: paragraph }
            }]
          }
        });
      }
    });

  } catch (error) {
    // Handle errors gracefully
    console.error(`Error processing email "${email.Name}":`, error.message);
    notionBlocks.push({
      object: "block",
      type: "paragraph",
      paragraph: {
        rich_text: [{
          type: "text",
          text: { content: `Error processing email: ${error.message}` }
        }]
      }
    });
  }
});

// Return just the blocks for the API endpoint
return { 
  json: { 
    children: notionBlocks 
  } 
};

