// Code Node: Prepare Tasks Array

// --- Get Data from Previous Nodes ---
// Adjust node names as needed!
const clientRelationId = $('Merge').first().json['notion client id'];
// Get campaign ID from the NEW Create Campaign Page node
const campaignRelationId = $('Create New Listing Ad Campaign').first().json.id;
const address = $('Typeform Trigger').first().json.Address || ''; // Get from Manual Trigger
const addressLine2 = $('Typeform Trigger').first().json['Address line 2'] || ''; // Get from Manual Trigger
const fullAddress = `${address} ${addressLine2}`.trim();
// Get the URL from the deliverables page node
const deliverablesPageUrl = $('Listing Ad - Create Client Deliverables Page for Listing Ad').first().json.url;

// --- Assignee IDs (Replace with your actual Notion User IDs) ---
const assigneeMatyas = "135d872b-594c-815e-8bb4-0002be26fee3"; // Placeholder
const assigneeFernanda = "135d872b-594c-815e-8bb4-0002be26fee3"; // Placeholder
const assigneeEmma = "135d872b-594c-815e-8bb4-0002be26fee3"; // Placeholder

// --- Due Date Calculation (Business Days from Now) ---
function getBusinessDaysDueDate(businessDays) {
  let d = new Date();
  let addedDays = 0;
  while (addedDays < businessDays) {
    d.setDate(d.getDate() + 1);
    const dayOfWeek = d.getDay(); // 0 = Sunday, 6 = Saturday
    if (dayOfWeek !== 0 && dayOfWeek !== 6) {
      addedDays++;
    }
  }
  return d.toISOString().split('T')[0]; // Return YYYY-MM-DD format
}

const dueDate2BusinessDays = getBusinessDaysDueDate(2);
const dueDate3BusinessDays = getBusinessDaysDueDate(3); // For Schedule Ad task

// --- Helper Function for Block Formatting ---
function formatBlock(type, content, options = {}) {
    const block = { object: "block", type: type };
    const data = {};

    if (type === 'paragraph' || type === 'heading_1' || type === 'heading_2' || type === 'heading_3' || type === 'bulleted_list_item' || type === 'numbered_list_item' || type === 'toggle' || type === 'quote' || type === 'callout') {
        if (options.contentRich) {
             data.rich_text = options.contentRich;
        } else {
             data.rich_text = [{ type: "text", text: { content: content || "" } }]; // Ensure content is not null/undefined
             if(options.annotations) {
                  data.rich_text[0].annotations = { ...options.annotations };
             }
        }
        if (options.color) data.color = options.color;
        if (options.is_toggleable) data.is_toggleable = options.is_toggleable;

    } else if (type === 'to_do') {
        // Handle potential simple string or complex rich text structure for to_do
        if (typeof content === 'string') {
             data.rich_text = [{ type: "text", text: { content: content } }];
        } else if (Array.isArray(content)) {
            data.rich_text = content; // Assume it's already a valid rich_text array
        } else {
            data.rich_text = [{ type: "text", text: { content: content || "" } }]; // Fallback
        }

         if(options.annotations && options.at) { // Simple annotation helper for first part
             if (data.rich_text && data.rich_text[0]?.text?.content) {
                 const originalText = data.rich_text[0].text.content;
                 const firstPart = originalText.substring(0, options.at);
                 const secondPart = originalText.substring(options.at);
                 data.rich_text = [
                     { type: "text", text: { content: firstPart }, annotations: { ...options.annotations } },
                     { type: "text", text: { content: secondPart } }
                 ];
             }
         } else if (options.annotations && data.rich_text && data.rich_text[0]) {
              data.rich_text[0].annotations = { ...options.annotations };
         }

        data.checked = options.checked || false;
        if (options.color) data.color = options.color;
        // Recursively format children if they exist
        if (options.children && Array.isArray(options.children)) {
           data.children = options.children.map(child => formatBlock(child.type, child.content, child));
        }

    } else if (type === 'image') {
        data.type = "external";
        // Use options.url if provided, otherwise use content. Default to a placeholder if neither.
        data.external = { url: options.url || content || 'https://via.placeholder.com/150' };
    } else if (type === 'divider') {
       // No data needed
    }
    // Add other block types as needed

    block[type] = data;
    return block;
}


// --- Define Blocks (Content for Tasks) ---
// Placeholders for generated content
const finalizeAdCopyBlocks = [
    formatBlock("heading_1", "Objective: Finalize Ad Copy"),
    formatBlock("paragraph", "AI-generated content will be appended here upon successful generation.")
];

const designCreativesBlocks = [
    formatBlock("heading_1", "Objective: Please design the Ad Creatives for this new listing."),
    formatBlock("heading_2", "These are the images uploaded by the client:"),
    // Get client images from Manual Trigger node
    formatBlock("image", $('Typeform Trigger').first().json['Upload THE BEST raw listing photo.'] || 'placeholder_url_1'),
    formatBlock("image", $('Typeform Trigger').first().json['Upload THE SECOND BEST raw listing photos.'] || 'placeholder_url_2'),
    formatBlock("image", $('Typeform Trigger').first().json['Upload THE THIRD BEST raw listing photo.'] || 'placeholder_url_3'),
    formatBlock("image", $('Typeform Trigger').first().json['Upload THE FOURTH BEST raw listing photo.'] || 'placeholder_url_4'),
    formatBlock("paragraph", "Generated ad creatives will be appended below later.")
];

const finalizeEmailBlocks = [
    formatBlock("heading_1", "Objective: Finalize Email Sequence"),
    formatBlock("paragraph", "AI-generated emails will be appended here upon successful generation.")
];

const sendToClientBlocks = [
     formatBlock("heading_1", "The Client Delivery Page has been created:"),
     formatBlock("paragraph", "", { contentRich: [{ type: 'text', text: { content: deliverablesPageUrl || 'Deliverables Page URL Not Found', link: deliverablesPageUrl ? { url: deliverablesPageUrl } : null }, annotations: { bold: true } }] }),
     formatBlock("paragraph", "Please add the “New Client Ad Delivery” Template to it and adjust the content and publish to be able to share with client")
];

const addEmailsToCRMBlocks = [
    formatBlock("heading_1", "Objective: Please upload the E-Mail Sequence into client’s CRM,")
];

// Blocks for "Schedule Ad on Meta" Task (Including QA Checklist)
const scheduleAdBlocks = [
    formatBlock("heading_1", "Objective: Please upload Schedule the Launch in Meta")
];

// --- Create the Tasks Array ---
const tasksToCreate = [
  {
    taskTitle: `Finalize Ad Copy for ${fullAddress}`,
    taskAssignee: [assigneeEmma],
    taskDueDate: dueDate2BusinessDays,
    taskBlocks: finalizeAdCopyBlocks,
    taskStatus: "Ready To-Do",
    taskPriority: "Medium"
  },
  {
    taskTitle: `Design the Ad Creatives for ${fullAddress}`,
    taskAssignee: [assigneeFernanda],
    taskDueDate: dueDate2BusinessDays,
    taskBlocks: designCreativesBlocks,
    taskStatus: "Ready To-Do",
    taskPriority: "Medium"
  },
   {
    taskTitle: `Finalize E-Mail Nurture Sequence for ${fullAddress}`,
    taskAssignee: [assigneeMatyas],
    taskDueDate: dueDate2BusinessDays,
    taskBlocks: finalizeEmailBlocks,
    taskStatus: "Ready To-Do",
    taskPriority: "Medium"
  },
   {
    taskTitle: `Send deliverables to the client for review and approval for ${fullAddress}`,
    taskAssignee: [assigneeFernanda],
    taskDueDate: dueDate2BusinessDays,
    taskBlocks: sendToClientBlocks,
    taskStatus: "BackLog",
    taskPriority: "Medium"
  },
  {
    taskTitle: `Add Sequence into client’s CRM for ${fullAddress}`,
    taskAssignee: [assigneeMatyas],
    taskDueDate: dueDate2BusinessDays,
    taskBlocks: addEmailsToCRMBlocks,
    taskStatus: "BackLog",
    taskPriority: "Medium"
  },
  {
    taskTitle: `Schedule Ad to be Published on Meta for ${fullAddress}`,
    taskAssignee: [assigneeMatyas],
