// Code Node: Aggregate Task IDs

const results = {
  adCopyTaskId: null,
  creativeTaskId: null,
  emailTaskId: null,
  sendToClientTaskId: null,
  addEmailsToCRMTaskId: null,
  scheduleAdTaskId: null
};

const createdTasks = $input.all();

for (const item of createdTasks) {
  // Adjust this path based on the ACTUAL output structure of your Notion Create Page node
  // It might be item.json.properties['Task Title'].title[0].plain_text or just item.json.name
  // Check the output of 'Notion - Create Tasks Loop' node to be sure.
  let taskTitle = '';
  try {
     taskTitle = item.json?.properties?.['Task Title']?.title?.[0]?.plain_text || item.json?.name || '';
  } catch (e) {
     console.warn("Error accessing title property for task item:", JSON.stringify(item.json));
  }

  if (!taskTitle) {
      console.warn("Task item missing title:", JSON.stringify(item.json));
      continue; // Skip if title isn't found
  }

  // Identify task and store ID
  if (taskTitle.includes("Finalize Ad Copy")) {
    results.adCopyTaskId = item.json.id;
  } else if (taskTitle.includes("Design the Ad Creatives")) {
    results.creativeTaskId = item.json.id;
  } else if (taskTitle.includes("Finalize E-Mail Nurture Sequence")) {
    results.emailTaskId = item.json.id;
  } else if (taskTitle.includes("Send deliverables to the client")) {
     results.sendToClientTaskId = item.json.id;
  } else if (taskTitle.includes("Add Sequence into client’s CRM")) {
     results.addEmailsToCRMTaskId = item.json.id;
  } else if (taskTitle.includes("Schedule Ad to be Published")) {
     results.scheduleAdTaskId = item.json.id;
  }
}

// Output a SINGLE item containing all the identified IDs
return [{ json: results }];
