// --- Code Node for Creating Seller Success Story Page ---

// Use try...catch for robust error handling
try {
  // --- 1. Get Data from Previous Nodes ---
  const formData = $('Typeform Trigger').first().json; // Original Typeform data

  // Get the Client ID from the Merge node
  const clientId = $('Merge').first().json["notion client id"];
  if (!clientId) {
    throw new Error("Could not find 'notion client id' from Merge node.");
  }

  // --- 2. Define Values for Properties ---
  let serviceRequestName; // Declare variable

  // Get the actual Client Name passed through the Merge node
  const clientName = $('Merge').first().json["first name"];
  if (!clientName) {
    // Fallback if clientName wasn't found in Merge node output
    console.warn("Client Name not found in Merge node output. Falling back to Typeform First Name.");
    const agentName = formData['*Your Name:*'] || 'Unknown Agent';
    serviceRequestName = `${agentName} - Seller Success Story`;
  } else {
    // Construct the desired service request name using the Client Name
    serviceRequestName = `${clientName} - Seller Success Story`;
  }

  const requestTypeValue = "Seller Success Story"; // Set the type specifically
  const submissionDateISO = new Date().toISOString(); // Get current date/time in UTC ISO format

  // --- 3. Generate Content Blocks (Page Body from Typeform) ---
  const contentBlocks = [];

  // Add a title block to the page body for clarity
  contentBlocks.push({
    object: "block",
    type: "heading_1",
    heading_1: {
      rich_text: [{
        type: "text",
        text: { content: serviceRequestName }
      }]
    }
  });

  // Add a divider
  contentBlocks.push({ object: "block", type: "divider", divider: {} });

  // Track which fields we've processed to catch any missed ones later
  const processedFields = new Set();

  // Define the exact sections from Typeform
  const sections = [
    { 
      title: "Property Address",
      fields: [
        "*Your Name:*",
        "Property Address:", 
        "City:", 
        "Country:"
      ]
    },
    { 
      title: "Property Stats",
      fields: [
        "Listed Price: ",
        "Sold Price:", 
        "Days on Market:", 
        "Number of Offers ", 
        "Number of Showing ", 
        "Any other stats that are relevant "
      ]
    },
    { 
      title: "Please share a Google Drive link to photos of the property featured in this case study.",
      fields: [
        "*Google Drive Link:*"
      ]
    },
    { 
      title: "Please provide a link to the MLS Listing/Sold Data from the Property.",
      fields: [
        "* Link to the Just Sold Data:*"
      ]
    },
    { 
      title: "The Backstory",
      fields: [
        "Client Name:", 
        "Client Email:",
        "Who were the sellers? (general profile â€“ downsizers, relocating, first-time sellers, etc.)",
        "What challenges did they face when deciding to sell?",
        "What was their biggest fear about the process?",
        "Why did they choose to work with you over another agent?",
        "What were their initial goals and expectations?"
      ]
    },
    { 
      title: "The Listing & Marketing Strategy",
      fields: [
        "What strategy did you use to *price* the home correctly?",
        "What marketing strategies made the biggest impact? (e.g., staging, professional photography, video tours, social media, targeted advertising, email marketing)",
        "Did you attract any out-of-town or unexpected buyers?",
        "How many showings and offers did you generate?",
        "How did you *position the home* to create urgency?"
      ]
    },
    { 
      title: "The Results",
      fields: [
        "What challenges (if any) arose, and how did you handle them?",
        "What was the final selling price vs. the original asking price?",
        "How* fast* did it sell compared to the market average?",
        "Did the seller receive *multiple offers*? (How many? Any over asking?)",
        "Did you help the sellers negotiate* better terms* beyond just price? (fewer conditions, faster closing, etc.)"
      ]
    },
    { 
      title: "The Emotional Impact",
      fields: [
        "What was the *seller's reaction* when they saw the results?",
        "How did this sale *change the seller's life*? (Relocation, retirement, dream home, financial freedom, etc.)",
        "What was the *most rewarding part* of this deal for you as their agent?",
        "Did the sellers leave a *testimonial* or share feedback about their experience?",
        "What's one thing the seller said that would *resonate with future* clients considering selling?"
      ]
    },
    { 
      title: "The Call to Action",
      fields: [
        "If another homeowner is thinking of selling, what's the #1 thing they should know about today's market?",
        "What's the biggest *misconception about selling* that you'd like to clear up?",
        "What advice would you give to someone on the fence about listing their home?"
      ]
    },
    { 
      title: "Bonus Items",
      fields: [
        "Was there anything* unique or surprising *about this sale? (e.g., sold in a weekend, buyer bought sight unseen, multiple offers in a slow market, etc.",
        "Any \"Before & After\" transformations? (e.g., \"We made one simple change, and it led to 10+ offers!\")",
        "Did you help the sellers *achieve something they didn't think was possible*?",
        "*Has this client given you a review?*",
        "*Is the client okay with their name being shared, or should it remain private? (Select One)*"
      ]
    }
  ];

  // Process each section
  for (const section of sections) {
    // Only add section if it has fields that exist in the form data
    let sectionHasContent = false;

    // Check if any fields in this section exist in formData
    for (const fieldName of section.fields) {
      // Check for exact match first
      if (fieldName in formData) {
        sectionHasContent = true;
        break;
      }

      // Check for match after cleaning asterisks
      const cleanFieldName = fieldName.replace(/\*/g, '').trim();
      for (const key of Object.keys(formData)) {
        const cleanKey = key.replace(/\*/g, '').trim();
        if (cleanKey === cleanFieldName) {
          sectionHasContent = true;
          break;
        }
      }

      if (sectionHasContent) break;
    }

    // Skip empty sections
    if (!sectionHasContent) continue;

    // Add section heading
    contentBlocks.push({
      object: "block",
      type: "heading_2",
      heading_2: {
        rich_text: [{ type: "text", text: { content: section.title } }],
        color: "default"
      }
    });

    // Process fields for this section
    for (const fieldName of section.fields) {
      // Clean up the field name by removing asterisks
      const cleanFieldName = fieldName.replace(/\*/g, '').trim();

      // Find the answer in formData by comparing cleaned versions of keys
      let answerKey = null;
      let answer = null;

      // Try exact match first
      if (fieldName in formData) {
        answerKey = fieldName;
        answer = formData[fieldName];
      } else {
        // Try cleaned match if exact match fails
        for (const key of Object.keys(formData)) {
          const cleanKey = key.replace(/\*/g, '').trim();
          if (cleanKey === cleanFieldName) {
            answerKey = key;
            answer = formData[key];
            break;
          }
        }
      }

      if (!answerKey) continue; // Skip if field not found

      // Mark this field as processed
      processedFields.add(answerKey);

      // Format the answer
      let formattedAnswer;
      if (answer === undefined || answer === null || answer === '') {
        formattedAnswer = 'Not provided';
      } else if (Array.isArray(answer)) {
        formattedAnswer = answer.join(', ');
      } else if (typeof answer === 'object' && answer !== null && !Array.isArray(answer) && !(answer instanceof Date)) {
        if (answer.hasOwnProperty('url') && answer.hasOwnProperty('name')) {
          formattedAnswer = answer.url; // Handle file objects
        } else {
          formattedAnswer = JSON.stringify(answer); // Stringify other objects
        }
      } else if (typeof answer === 'boolean') {
        formattedAnswer = answer ? 'Yes' : 'No';
      } else {
        formattedAnswer = String(answer);
      }

      // Check for image URLs
      const isImageUrl = typeof formattedAnswer === 'string' && (
        formattedAnswer.includes('api.typeform.com/responses/files') ||
        (formattedAnswer.startsWith('http') && (
          formattedAnswer.endsWith('.jpg') || 
          formattedAnswer.endsWith('.png') ||
          formattedAnswer.endsWith('.jpeg') || 
          formattedAnswer.endsWith('.gif')
        ))
      );

      // Handle images or regular text differently
      if (isImageUrl) {
        // For images, add question as bold text first
        contentBlocks.push({
          object: "block", 
          type: "paragraph",
          paragraph: {
            rich_text: [
              { type: "text", text: { content: cleanFieldName + ":" }, annotations: { bold: true } }
            ],
            color: "default"
          }
        });

        // Then add the image
        contentBlocks.push({
          object: "block", 
          type: "image", 
          image: { 
            type: "external", 
            external: { url: formattedAnswer } 
          }
        });
      } else {
        // Add the question and answer in a single paragraph (bold question, regular answer)
        contentBlocks.push({
          object: "block", 
          type: "paragraph",
          paragraph: {
            rich_text: [
              { type: "text", text: { content: cleanFieldName + ": " }, annotations: { bold: true } },
              { type: "text", text: { content: formattedAnswer } }
            ],
            color: "default"
          }
        });
      }
    }
  }

  // --- FAILSAFE: Check for any fields not processed ---
  const unprocessedFields = Object.keys(formData).filter(key => 
    !processedFields.has(key) && 
    !key.startsWith('_') && // Skip metadata fields
    key !== 'markdown' && 
    key !== 'error' && 
    key !== 'event_id' && 
    key !== 'event_type' && 
    key !== 'form_response'
  );

  if (unprocessedFields.length > 0) {
    // Add a section for additional fields
    contentBlocks.push({
      object: "block",
      type: "heading_2",
      heading_2: {
        rich_text: [{ type: "text", text: { content: "Additional Information" } }],
        color: "default"
      }
    });

    // Process each unprocessed field
    for (const fieldName of unprocessedFields) {
      const answer = formData[fieldName];
      const cleanFieldName = fieldName.replace(/\*/g, '').trim();

      // Format the answer (same as above)
      let formattedAnswer;
      if (answer === undefined || answer === null || answer === '') {
        formattedAnswer = 'Not provided';
      } else if (Array.isArray(answer)) {
        formattedAnswer = answer.join(', ');
      } else if (typeof answer === 'object' && answer !== null && !Array.isArray(answer) && !(answer instanceof Date)) {
        if (answer.hasOwnProperty('url') && answer.hasOwnProperty('name')) {
          formattedAnswer = answer.url;
        } else {
          formattedAnswer = JSON.stringify(answer);
        }
      } else if (typeof answer === 'boolean') {
        formattedAnswer = answer ? 'Yes' : 'No';
      } else {
        formattedAnswer = String(answer);
      }

      // Check for image URLs (same as above)
      const isImageUrl = typeof formattedAnswer === 'string' && (
        formattedAnswer.includes('api.typeform.com/responses/files') ||
        (formattedAnswer.startsWith('http') && (
          formattedAnswer.endsWith('.jpg') || 
          formattedAnswer.endsWith('.png') ||
          formattedAnswer.endsWith('.jpeg') || 
          formattedAnswer.endsWith('.gif')
        ))
      );

      // Handle images or regular text (same as above)
      if (isImageUrl) {
        contentBlocks.push({
          object: "block", 
          type: "paragraph",
          paragraph: {
            rich_text: [
              { type: "text", text: { content: cleanFieldName + ":" }, annotations: { bold: true } }
            ],
            color: "default"
          }
        });
        contentBlocks.push({
          object: "block", 
          type: "image", 
          image: { 
            type: "external", 
            external: { url: formattedAnswer } 
          }
        });
      } else {
        contentBlocks.push({
          object: "block", 
          type: "paragraph",
          paragraph: {
            rich_text: [
              { type: "text", text: { content: cleanFieldName + ": " }, annotations: { bold: true } },
              { type: "text", text: { content: formattedAnswer } }
            ],
            color: "default"
          }
        });
      }
    }
  }

  // Count final number of blocks for debugging
  console.log(`Total content blocks: ${contentBlocks.length}`);
  console.log(`Processed ${processedFields.size} fields out of ${Object.keys(formData).length} total fields`);

  // Check if we're over the limit
  if (contentBlocks.length > 95) {
    console.warn(`Warning: Block count (${contentBlocks.length}) is approaching the Notion limit of 100`);
  }

  // --- 4. Prepare Data for Notion API ---
  const notionDataPayload = {
    parent: {
      // The Database ID for your Service Requests database
      database_id: "1b41d08c4f5a80c1a843f04215bd0fd0"
    },
    icon: {
      type: "emoji",
      emoji: "ðŸ "  // House emoji for seller story
    },
    properties: {
      // Title Property: Name
      "Name": {
        title: [{ text: { content: serviceRequestName } }]
      },
      // Select Property: Request Type
      "Request Type": {
        select: {
          name: requestTypeValue
        }
      },
      // Relation Property: Client
      "Client": {
        relation: [{ id: clientId }]
      },
      // Date Property: Submission Date
      "Submission Date": {
        date: {
          start: submissionDateISO
        }
      }
    },
    // Page Body Content: Children
    children: contentBlocks
  };

  // --- 5. Return the Payload ---
  // Output the data needed for the subsequent HTTP Request node
  return { json: { notionData: notionDataPayload } };

} catch (error) {
  // --- Error Handling ---
  console.error('Error preparing Service Request Notion data:', error.message);
  throw error; // Stop workflow on error
}

