// Code Node: Seller Success Story Task Generator

try {
    // --- 1. Get Data from Previous Nodes ---
    const mergeOutput = $('Merge').first().json;
    const clientRelationId = mergeOutput['notion client id'];
    const clientName = mergeOutput['first name'];

    if (!clientRelationId) { throw new Error("Could not get clientRelationId from Merge node."); }
    if (!clientName) { throw new Error("Could not get 'first name' from Merge node."); }

    // --- Get Service Request ID and URL ---
    const serviceRequestNode = $('Create New Service Request');
    const serviceRequestOutput = serviceRequestNode.first();
    const serviceRelationId = serviceRequestOutput?.json?.id;
    const serviceRequestUrl = serviceRequestOutput?.json?.url;

    if (!serviceRelationId || typeof serviceRelationId !== 'string') {
        throw new Error(`Failed to get a valid page ID from node 'Create New Service Request'.`);
    }

    // --- Get Original Typeform Data ---
    const formData = $('Typeform Trigger').first().json;
    if (!formData) {
        throw new Error("Could not get data from Typeform Trigger node.");
    }

    // Extract relevant information from form data
    const agentName = formData["*Your Name:*"] || clientName;
    const propertyAddress = formData["Property Address:"] || "";
    const city = formData["City:"] || "";
    const country = formData["Country:"] || "";
    const listedPrice = formData["Listed Price: "] || "";
    const soldPrice = formData["Sold Price:"] || "";
    const daysOnMarket = formData["Days on Market:"] || "";
    const numberOfOffers = formData["Number of Offers "] || "";
    const numberOfShowings = formData["Number of Showing "] || "";
    const clientSellerName = formData["Client Name:"] || "";
    const googleDriveLink = formData["*Google Drive Link:*"] || "";
    const justSoldLink = formData["* Link to the Just Sold Data:*"] || "";
    const sellerProfile = formData["Who were the sellers? (general profile â€“ downsizers, relocating, first-time sellers, etc.)"] || "";
    const marketingStrategy = formData["What marketing strategies made the biggest impact? (e.g., staging, professional photography, video tours, social media, targeted advertising, email marketing)"] || "";

    // Property summary for tasks
    const propertySummary = `${propertyAddress}, ${city}`;

    // --- 2. Define Assignee IDs ---
    const assigneeMatyas = "135d872b-594c-815e-8bb4-0002be26fee3";
    const assigneeFernanda = "135d872b-594c-815e-8bb4-0002be26fee3";

    // --- 3. Due Date Calculation ---
    function getBusinessDaysDueDate(businessDays) {
        let d = new Date(); let addedDays = 0;
        if (businessDays === 0) { const day = d.getDay(); if (day !== 0 && day !== 6) return d.toISOString().split('T')[0]; else businessDays = 1; }
        while (addedDays < businessDays) { d.setDate(d.getDate() + 1); const day = d.getDay(); if (day !== 0 && day !== 6) addedDays++; }
        return d.toISOString().split('T')[0];
    }

    const dueDateToday = getBusinessDaysDueDate(0);
    const dueDateNextDay = getBusinessDaysDueDate(1);
    const dueDate2Days = getBusinessDaysDueDate(2);
    const dueDate3Days = getBusinessDaysDueDate(3);
    const dueDate5Days = getBusinessDaysDueDate(5);

    // --- 4. Helper Functions ---
    function formatBlock(type, content, options = {}) { 
        const block = { object: "block", type: type }; 
        const data = {}; 
        if (type === 'paragraph' || type.startsWith('heading') || type.endsWith('list_item') || ['toggle', 'quote', 'callout'].includes(type)) { 
            data.rich_text = options.contentRich || [{ type: "text", text: { content: content || "" } }]; 
            if(options.annotations && !options.contentRich) {data.rich_text[0].annotations = { ...options.annotations };} 
            if (options.color) data.color = options.color; 
            if (options.is_toggleable) data.is_toggleable = options.is_toggleable; 
        } else if (type === 'to_do') { 
            data.rich_text = typeof content === 'string' ? [{ type: "text", text: { content: content } }] : (Array.isArray(content) ? content : [{ type: "text", text: { content: content || "" } }]); 
            if(options.annotations && data.rich_text[0]) {data.rich_text[0].annotations = { ...options.annotations };} 
            data.checked = options.checked || false; 
            if (options.color) data.color = options.color; 
            if (options.children && Array.isArray(options.children)) { 
                data.children = options.children.map(child => formatBlock(child.type, child.content, child)); 
            } 
        } else if (type === 'image') { 
            data.type = "external"; 
            data.external = { url: options.url || content || 'https://via.placeholder.com/150' }; 
        } else if (type === 'divider') {} 

        block[type] = data; 
        return block; 
    }

    // Create service request link block
    function createServiceRequestLinkBlocks() {
        const blocks = [];
        blocks.push(formatBlock("divider", {}));
        blocks.push(formatBlock("heading_3", "Client Information"));

        if (serviceRequestUrl) {
            // Create rich text with link
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "View Client Service Request Details",
                                link: { url: serviceRequestUrl }
                            },
                            annotations: {
                                bold: true,
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        } else {
            blocks.push(formatBlock("paragraph", "Service Request URL not available"));
        }

        return blocks;
    }

    // Create form data blocks for success story information
    function createSuccessStoryDataBlocks() {
        const blocks = [];
        blocks.push(formatBlock("divider", {}));
        blocks.push(formatBlock("heading_3", "Seller Success Story Information"));

        // Property Details
        blocks.push(formatBlock("paragraph", `Property Address: ${propertyAddress}, ${city}, ${country}`));
        blocks.push(formatBlock("paragraph", `Listed Price: $${listedPrice} | Sold Price: $${soldPrice}`));
        blocks.push(formatBlock("paragraph", `Days on Market: ${daysOnMarket} | Number of Offers: ${numberOfOffers} | Number of Showings: ${numberOfShowings}`));

        // Seller Details
        blocks.push(formatBlock("divider", {}));
        blocks.push(formatBlock("heading_3", "Seller Details"));

        if (clientSellerName) {
            blocks.push(formatBlock("paragraph", `Seller Name: ${clientSellerName}`));
        }

        if (sellerProfile) {
            blocks.push(formatBlock("paragraph", `Seller Profile: ${sellerProfile}`));
        }

        if (marketingStrategy) {
            blocks.push(formatBlock("paragraph", `Marketing Strategy: ${marketingStrategy}`));
        }

        // Resource Links
        blocks.push(formatBlock("divider", {}));
        blocks.push(formatBlock("heading_3", "Resources"));

        // Google Drive Link
        if (googleDriveLink) {
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "Google Drive Photos: ",
                            }
                        },
                        {
                            type: "text",
                            text: {
                                content: googleDriveLink,
                                link: { url: googleDriveLink }
                            },
                            annotations: {
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        }

        // Just Sold Data Link
        if (justSoldLink) {
            blocks.push({
                object: "block",
                type: "paragraph",
                paragraph: {
                    rich_text: [
                        {
                            type: "text",
                            text: {
                                content: "Just Sold Data: ",
                            }
                        },
                        {
                            type: "text",
                            text: {
                                content: justSoldLink,
                                link: { url: justSoldLink }
                            },
                            annotations: {
                                color: "blue"
                            }
                        }
                    ]
                }
            });
        }

        return blocks;
    }

    const serviceRequestLinkBlocks = createServiceRequestLinkBlocks();
    const successStoryDataBlocks = createSuccessStoryDataBlocks();

    // --- 5. Create Tasks Array ---
    let tasksToCreate = [
        // Task 1: Draft Seller Success Story
        { 
            taskTitle: `Seller Success Story | ${agentName} | Draft Story | ${propertySummary}`, 
            taskAssignee: [assigneeMatyas], 
            taskPriority: "Medium", 
            taskDueDate: dueDate3Days, 
            taskStatus: "Ready To-Do", 
            taskBlocks: [
                formatBlock("paragraph", "Draft a compelling seller success story based on the information provided."),
                formatBlock("paragraph", "Highlight the marketing strategy, days on market, and final sale price."),
                formatBlock("paragraph", "Include any relevant quotes or testimonials from the seller."),
                ...successStoryDataBlocks,
                ...serviceRequestLinkBlocks
            ] 
        },

        // Task 2: Create Design for Seller Success Story
        { 
            taskTitle: `Seller Success Story | ${agentName} | Create Design | ${propertySummary}`, 
            taskAssignee: [assigneeFernanda], 
            taskPriority: "Medium", 
            taskDueDate: dueDate3Days, 
            taskStatus: "Ready To-Do", 
            taskBlocks: [
                formatBlock("paragraph", "Design a visually appealing layout for the seller success story."),
                formatBlock("paragraph", "Incorporate property photos and sold statistics in an engaging format."),
                formatBlock("paragraph", "Create versions optimized for different platforms (social media, email newsletter, website)."),
                ...successStoryDataBlocks,
                ...serviceRequestLinkBlocks
            ] 
        },

        // Task 3: Send Success Story for Approval
        { 
            taskTitle: `Seller Success Story | ${agentName} | Send for Approval | ${propertySummary}`, 
            taskAssignee: [assigneeFernanda], 
            taskPriority: "Medium", 
            taskDueDate: dueDate5Days, 
            taskStatus: "Ready To-Do", 
            taskBlocks: [
                formatBlock("paragraph", "Send the drafted success story and design to the agent for review and approval."),
                formatBlock("paragraph", "Confirm all statistics and seller information are accurate."),
                formatBlock("paragraph", "Request feedback and make any necessary revisions before final publication."),
                ...serviceRequestLinkBlocks
            ] 
        }
    ];

    // --- 6. Add Common Properties to Each Task ---
    tasksToCreate.forEach(task => {
        task.taskClientRelationId = [clientRelationId];
        task.taskServiceRelationId = [serviceRelationId];
        task.taskTags = ["Seller Success Story"];
    });

    // --- 7. Return the Array of Tasks ---
    return tasksToCreate;

} catch (error) {
    console.error("Error in Seller Success Story Task Generator:", error.message);
    throw error; // Re-throw error to stop workflow
}
